import { createSlice } from "@reduxjs/toolkit";

const initialState={
    value:100,
}

 export const CounterSlice=createSlice({
    name:"counter",
    initialState,
    reducers:{
        increment:(state)=>{
            state.value += 1;
        },
        decrement:(state)=>{
            state.value -=1;
        }
    }
})
// action ki help se sare function ki value bhar aa jati he 
export const {increment,decrement}= CounterSlice.actions;
// reducer yaha se bhar aa jati he functionty for reducer 
export default CounterSlice.reducer;
