import React from "react";
import { useSelector } from "react-redux";

const Counter=()=>{
    
    // slice ki kise particular value ke liye useSelector hook use karte he kise slice se undr se data fetch karke lana he to
     const count = useSelector((state)=>state.counter.value);

return(
    <div>
      <button>
        Increment
      </button>
    <br/>
    <br/>
    <div>{count}</div>
    <br/>
    <br/>
    <button>
       Decrement
      </button>
    </div>
)
}
export default Counter;
