import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { decrement, increment } from "../redux/slices/CounterSlice";

const Counter=()=>{
    
    // slice ki kise particular value ke liye useSelector hook use karte he kise slice se undr se data fetch karke lana he to
     const count = useSelector((state)=>state.counter.value);
     const dispatch=useDispatch(); 
return(
    <div>
{/* usedispatch ki help se apn call krte he inki value increment aur decrement ki */}
      <button
      onClick={()=>dispatch(increment())}>
        Increment
      </button>
    <br/>
    <br/>
    <div>{count}</div>
    <br/>
    <br/>
    <button
    onClick={()=>dispatch(decrement())}>
       Decrement
      </button>
    </div>
)
}
export default Counter;
